import React, { useState, useEffect, useRef } from 'react';
// import './Manufacturing.css'

// Dummy data for the 'Finished Product' dropdown
const stockLedger = [
  { id: 1, name: 'Assembled Gaming PC' },
  { id: 2, name: 'Wooden Bookshelf' },
  { id: 3, name: 'Metal Desk Frame' },
];

// Helper function to generate a new, sequential BOM ID
function generateBOMId(lastId) {
  const nextNumber = (parseInt(lastId?.split('-')[1] || '0', 10) + 1);
  return `BOM-${nextNumber.toString().padStart(6, '0')}`;
}

const BillOfMaterials = () => {
  // Main state for all Bill of Materials data
  const [boms, setBOMs] = useState([]);
  
  // State for the list view and view toggling
  const [search, setSearch] = useState('');
  const [showEdit, setShowEdit] = useState(false);
  const [editingBOM, setEditingBOM] = useState(null);

  // State for the Manufacturing Order display
  const [selectedBOMId, setSelectedBOMId] = useState('');
  const [moComponents, setMoComponents] = useState([]);
  const [moWorkOrders, setMoWorkOrders] = useState([]);

  // State for the form fields when creating or editing a BOM
  const isNew = !editingBOM;
  const [bomId, setBomId] = useState('');
  const [finishedProduct, setFinishedProduct] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [reference, setReference] = useState('');
  const [components, setComponents] = useState([]);
  const [workOrders, setWorkOrders] = useState([]);

  // New: for better interactivity
  const [error, setError] = useState('');
  const [rowHighlight, setRowHighlight] = useState(null);
  const finishedProductRef = useRef(null);

  // Focus on first field when entering edit/new
  useEffect(() => {
    if (showEdit && finishedProductRef.current) {
      finishedProductRef.current.focus();
    }
  }, [showEdit]);

  // Populate the form when switching to 'New/Edit' view
  useEffect(() => {
    if (showEdit) {
      if (editingBOM) { // Populate with data of the BOM being edited
        setBomId(editingBOM.id);
        setFinishedProduct(editingBOM.finishedProduct);
        setQuantity(editingBOM.quantity);
        setReference(editingBOM.reference);
        setComponents(editingBOM.components || []);
        setWorkOrders(editingBOM.workOrders || []);
      } else { // Reset fields for a new BOM
        setBomId(generateBOMId(boms.length ? boms[boms.length - 1].id : null));
        setFinishedProduct('');
        setQuantity(1);
        setReference('');
        setComponents([]);
        setWorkOrders([]);
      }
      setError('');
    }
  }, [showEdit, editingBOM, boms]);

  // Update the Manufacturing Order display when a BOM is selected
  useEffect(() => {
    const bom = boms.find(b => b.id === selectedBOMId);
    if (bom) {
      setMoComponents(bom.components || []);
      setMoWorkOrders(bom.workOrders || []);
    } else {
      setMoComponents([]);
      setMoWorkOrders([]);
    }
  }, [selectedBOMId, boms]);

  // Dynamically filters the BOM list based on the search input
  const filteredBOMs = boms.filter(bom =>
    bom.finishedProduct.toLowerCase().includes(search.toLowerCase()) ||
    bom.reference.toLowerCase().includes(search.toLowerCase())
  );

  // Handlers for the dynamic "Components" table in the form
  const addComponent = () => setComponents([...components, { component: '', toConsume: 1, units: '' }]);
  const updateComponent = (idx, field, value) => {
    const updated = components.map((row, i) => i === idx ? { ...row, [field]: value } : row);
    setComponents(updated);
  };
  const removeComponent = idx => setComponents(components.filter((_, i) => i !== idx));

  // Handlers for the dynamic "Work Orders" table in the form
  const addWorkOrder = () => setWorkOrders([...workOrders, { operation: '', workCenter: '', duration: '' }]);
  const updateWorkOrder = (idx, field, value) => {
    const updated = workOrders.map((row, i) => i === idx ? { ...row, [field]: value } : row);
    setWorkOrders(updated);
  };
  const removeWorkOrder = idx => setWorkOrders(workOrders.filter((_, i) => i !== idx));

  // Validate form fields before save
  const isFormInvalid = !finishedProduct || !quantity || quantity <= 0;

  // Save with confirmation and error feedback
  const handleSave = () => {
    setError('');
    if (isFormInvalid) {
      setError('Please select a Finished Product and set a valid Quantity.');
      return;
    }
    const bomToSave = { id: bomId, finishedProduct, quantity: parseFloat(quantity), reference, components, workOrders };
    if (!isNew) {
      if (!window.confirm('Are you sure you want to overwrite this BOM?')) return;
      setBOMs(prev => prev.map(b => b.id === bomToSave.id ? bomToSave : b));
    } else {
      setBOMs(prev => [...prev, bomToSave]);
    }
    setShowEdit(false);
    setEditingBOM(null);
  };

  // Confirm before canceling edits
  const handleCancel = () => {
    if (window.confirm('Discard changes to the Bill of Materials?')) {
      setShowEdit(false);
      setEditingBOM(null);
      setError('');
    }
  };

  // Handle row hover and click-to-edit
  const handleRowMouseEnter = idx => setRowHighlight(idx);
  const handleRowMouseLeave = () => setRowHighlight(null);

  const handleEdit = (bom) => { setEditingBOM(bom); setShowEdit(true); };
  const handleNew = () => { setEditingBOM(null); setShowEdit(true); };

  return (
    <div className="manufacturing-container">
      <a href="#" onClick={() => setShowEdit(false)} className="back-link">‚Üê Back to Dashboard</a>

      <style>{`
        .highlighted-row { background: #e6f7ff; transition: background 0.2s; }
        button:disabled { background: #ccc; cursor: not-allowed; }
        .error-message { color: #d32f2f; margin: 8px 0 0 0; }
      `}</style>

      {!showEdit ? (
        <>
          {/* Bills of Materials List View */}
          <section className="card">
            <div className="card-header">
              <h1>Bills of Materials</h1>
              <div className="actions">
                <input
                  type="text"
                  placeholder="Search Finished Product or Reference..."
                  value={search}
                  onChange={e => setSearch(e.target.value)}
                  className="search-input"
                />
                <button onClick={handleNew} className="btn btn-primary">New</button>
              </div>
            </div>
            <table className="data-table">
              <thead>
                <tr><th>BOM ID</th><th>Finished Product</th><th>Quantity</th><th>Reference</th><th>Actions</th></tr>
              </thead>
              <tbody>
                {filteredBOMs.length > 0 ? filteredBOMs.map((bom, idx) => (
                  <tr
                    key={bom.id}
                    className={rowHighlight === idx ? 'highlighted-row' : ''}
                    onMouseEnter={() => handleRowMouseEnter(idx)}
                    onMouseLeave={handleRowMouseLeave}
                    onClick={() => handleEdit(bom)}
                    style={{ cursor: 'pointer' }}
                  >
                    <td>{bom.id}</td>
                    <td>{bom.finishedProduct}</td>
                    <td>{bom.quantity}</td>
                    <td>{bom.reference}</td>
                    <td>
                      <button
                        onClick={e => { e.stopPropagation(); handleEdit(bom); }}
                        className="btn btn-secondary"
                      >Edit</button>
                    </td>
                  </tr>
                )) : (
                  <tr><td colSpan="5" className="empty-state">No Bills of Materials found.</td></tr>
                )}
              </tbody>
            </table>
          </section>

          {/* Manufacturing Order Display View */}
          <section className="card">
            <div className="card-header">
              <h2>Manufacturing Order</h2>
              <div className="actions">
                <label htmlFor="bom-select">Select BOM</label>
                <select id="bom-select" value={selectedBOMId} onChange={e => setSelectedBOMId(e.target.value)}>
                  <option value="">Select a BOM to view its template</option>
                  {boms.map(bom => <option key={bom.id} value={bom.id}>{`${bom.id} - ${bom.finishedProduct}`}</option>)}
                </select>
              </div>
            </div>
            <div className="mo-section">
                <h3>Components</h3>
                <table className="data-table">
                  <thead><tr><th>Component</th><th>To Consume</th><th>Units</th></tr></thead>
                  <tbody>
                    {moComponents.length > 0 ? moComponents.map((c, i) => <tr key={i}><td>{c.component}</td><td>{c.toConsume}</td><td>{c.units}</td></tr>) : <tr><td colSpan="3" className="empty-state">Select a BOM to see components.</td></tr>}
                  </tbody>
                </table>
            </div>
            <div className="mo-section">
                <h3>Work Orders</h3>
                <table className="data-table">
                  <thead><tr><th>Operation</th><th>Work Center</th><th>Expected Duration</th></tr></thead>
                  <tbody>
                    {moWorkOrders.length > 0 ? moWorkOrders.map((wo, i) => <tr key={i}><td>{wo.operation}</td><td>{wo.workCenter}</td><td>{wo.duration}</td></tr>) : <tr><td colSpan="3" className="empty-state">Select a BOM to see work orders.</td></tr>}
                  </tbody>
                </table>
            </div>
          </section>
        </>
      ) : (
        /* New/Edit Bill of Materials Form View */
        <section className="card">
            <h1>{isNew ? 'Create' : 'Edit'} Bill of Materials</h1>
            {error && <div className="error-message">{error}</div>}
            <div className="form-grid">
              <div><label>BOM ID</label><input value={bomId} readOnly /></div>
              <div>
                <label>Finished Product</label>
                <select 
                  ref={finishedProductRef}
                  value={finishedProduct} 
                  onChange={e => setFinishedProduct(e.target.value)}
                >
                  <option value="">Select a Product</option>
                  {stockLedger.map(item => <option key={item.id} value={item.name}>{item.name}</option>)}
                </select>
              </div>
              <div><label>Quantity</label><input type="number" step="0.01" min="0" value={quantity} onChange={e => setQuantity(e.target.value)} /></div>
              <div><label>Reference</label><input type="text" maxLength={8} value={reference} onChange={e => setReference(e.target.value)} /></div>
            </div>

            <div className="dynamic-table-section">
              <h3>Components</h3>
              <table className="data-table">
                <thead><tr><th>Component</th><th>To Consume</th><th>Units</th><th>Action</th></tr></thead>
                <tbody>
                  {components.map((row, idx) => (
                    <tr key={idx}>
                      <td><input value={row.component} onChange={e => updateComponent(idx, 'component', e.target.value)} placeholder="e.g., Motherboard"/></td>
                      <td><input type="number" step="0.01" min="0" value={row.toConsume} onChange={e => updateComponent(idx, 'toConsume', e.target.value)} /></td>
                      <td><input value={row.units} onChange={e => updateComponent(idx, 'units', e.target.value)} placeholder="e.g., pcs"/></td>
                      <td><button onClick={() => removeComponent(idx)} className="btn btn-danger">Remove</button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <button onClick={addComponent} className="btn btn-secondary">Add Component</button>
            </div>

            <div className="dynamic-table-section">
              <h3>Work Orders</h3>
              <table className="data-table">
                <thead><tr><th>Operation</th><th>Work Center</th><th>Expected Duration (hours)</th><th>Action</th></tr></thead>
                <tbody>
                  {workOrders.map((row, idx) => (
                    <tr key={idx}>
                      <td><input value={row.operation} onChange={e => updateWorkOrder(idx, 'operation', e.target.value)} placeholder="e.g., Assembly"/></td>
                      <td><input value={row.workCenter} onChange={e => updateWorkOrder(idx, 'workCenter', e.target.value)} placeholder="e.g., Assembly Line 1"/></td>
                      <td><input type="number" step="0.01" min="0" value={row.duration} onChange={e => updateWorkOrder(idx, 'duration', e.target.value)} /></td>
                      <td><button onClick={() => removeWorkOrder(idx)} className="btn btn-danger">Remove</button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <button onClick={addWorkOrder} className="btn btn-secondary">Add Work Order</button>
            </div>
            
            <div className="form-actions">
              <button onClick={handleSave} className="btn btn-primary" disabled={isFormInvalid}>Save Bill of Materials</button>
              <button onClick={handleCancel} className="btn">Cancel</button>
            </div>
        </section>
      )}
    </div>
  );
};

export default BillOfMaterials;
