import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [activeTab, setActiveTab] = useState('login');
  const [loginData, setLoginData] = useState({ userId: '', password: '' });
  const [signupData, setSignupData] = useState({
    userId: '', password: '', confirmPassword: '', name: '', email: '', role: 'User'
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const { login, signup } = useAuth();
  const navigate = useNavigate();

  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleSignupChange = (e) => {
    setSignupData({ ...signupData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(loginData.userId, loginData.password);
      if (result.success) navigate('/dashboard');
      else setError(result.error || 'Login failed');
    } catch {
      setError('An error occurred during login');
    } finally {
      setLoading(false);
    }
  };

  const handleSignupSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (signupData.password !== signupData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    try {
      const result = await signup(signupData);
      if (result.success) navigate('/dashboard');
      else setError(result.error || 'Signup failed');
    } catch {
      setError('An error occurred during signup');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-50">
      <div className="w-full max-w-md p-8 bg-white rounded-2xl shadow-lg">
        {/* Tabs */}
        <div className="flex mb-6">
          <button onClick={() => setActiveTab('login')} className={`flex-1 py-2 ${activeTab==='login'?'font-bold border-b-2 border-blue-600':'text-gray-500'}`}>Login</button>
          <button onClick={() => setActiveTab('signup')} className={`flex-1 py-2 ${activeTab==='signup'?'font-bold border-b-2 border-blue-600':'text-gray-500'}`}>Sign Up</button>
        </div>

        {/* Login Form */}
        {activeTab==='login' && (
          <form onSubmit={handleLoginSubmit} className="space-y-4">
            <input
              type="text"
              name="userId"
              placeholder="User ID"
              value={loginData.userId}
              onChange={handleLoginChange}
              className="w-full px-4 py-2 border rounded-lg"
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={loginData.password}
              onChange={handleLoginChange}
              className="w-full px-4 py-2 border rounded-lg"
              required
            />
            {error && <p className="text-red-600 text-sm">{error}</p>}
            <button type="submit" disabled={loading} className="w-full py-2 bg-blue-600 text-white rounded-lg">
              {loading ? 'Signing in...' : 'Login'}
            </button>
          </form>
        )}

        {/* Signup Form */}
        {activeTab==='signup' && (
          <form onSubmit={handleSignupSubmit} className="space-y-4">
            <input type="text" name="userId" placeholder="User ID" value={signupData.userId} onChange={handleSignupChange} required className="w-full px-4 py-2 border rounded-lg"/>
            <input type="text" name="name" placeholder="Full Name" value={signupData.name} onChange={handleSignupChange} required className="w-full px-4 py-2 border rounded-lg"/>
            <input type="email" name="email" placeholder="Email" value={signupData.email} onChange={handleSignupChange} required className="w-full px-4 py-2 border rounded-lg"/>
            <input type="password" name="password" placeholder="Password" value={signupData.password} onChange={handleSignupChange} required className="w-full px-4 py-2 border rounded-lg"/>
            <input type="password" name="confirmPassword" placeholder="Confirm Password" value={signupData.confirmPassword} onChange={handleSignupChange} required className="w-full px-4 py-2 border rounded-lg"/>
            <select name="role" value={signupData.role} onChange={handleSignupChange} className="w-full px-4 py-2 border rounded-lg">
              <option value="User">User</option>
              <option value="Manager">Manager</option>
              <option value="Admin">Admin</option>
            </select>
            {error && <p className="text-red-600 text-sm">{error}</p>}
            <button type="submit" disabled={loading} className="w-full py-2 bg-green-600 text-white rounded-lg">
              {loading ? 'Creating Account...' : 'Sign Up'}
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default Login;
