import React, { useState, useEffect } from 'react';
import './Manufacturing.css'; // <-- Add this import

// Dummy stock ledger data for Finished Product dropdown
const stockLedger = [
  { id: 1, name: 'Widget A' },
  { id: 2, name: 'Widget B' },
  { id: 3, name: 'Widget C' },
];

function generateBOMId(lastId) {
  const next = (parseInt(lastId?.split('-')[1] || '0', 10) + 1).toString().padStart(6, '0');
  return `BOM-${next}`;
}

const BillOfMaterials = () => {
  const [boms, setBOMs] = useState([]);
  const [search, setSearch] = useState('');
  const [showEdit, setShowEdit] = useState(false);
  const [editingBOM, setEditingBOM] = useState(null);

  // Manufacturing Order state
  const [selectedBOMId, setSelectedBOMId] = useState('');
  const [moComponents, setMoComponents] = useState([]);
  const [moWorkOrders, setMoWorkOrders] = useState([]);

  // Form states
  const isNew = !editingBOM;
  const [bomId, setBomId] = useState('');
  const [finishedProduct, setFinishedProduct] = useState('');
  const [quantity, setQuantity] = useState('');
  const [reference, setReference] = useState('');
  const [components, setComponents] = useState([]);
  const [workOrders, setWorkOrders] = useState([]);

  // When editing, populate form fields
  useEffect(() => {
    if (showEdit) {
      if (editingBOM) {
        setBomId(editingBOM.id);
        setFinishedProduct(editingBOM.finishedProduct);
        setQuantity(editingBOM.quantity);
        setReference(editingBOM.reference);
        setComponents(editingBOM.components || []);
        setWorkOrders(editingBOM.workOrders || []);
      } else {
        setBomId(generateBOMId(boms.length ? boms[boms.length - 1].id : null));
        setFinishedProduct('');
        setQuantity('');
        setReference('');
        setComponents([]);
        setWorkOrders([]);
      }
    }
  }, [showEdit, editingBOM, boms]);

  // Manufacturing Order: auto-populate tables when BOM selected
  useEffect(() => {
    const bom = boms.find(b => b.id === selectedBOMId);
    if (bom) {
      setMoComponents(bom.components || []);
      setMoWorkOrders(bom.workOrders || []);
    } else {
      setMoComponents([]);
      setMoWorkOrders([]);
    }
  }, [selectedBOMId, boms]);

  // List view filter
  const filteredBOMs = boms.filter(bom =>
    bom.finishedProduct.toLowerCase().includes(search.toLowerCase()) ||
    bom.reference.toLowerCase().includes(search.toLowerCase())
  );

  // Handlers (no changes to logic)
  const addComponent = () => setComponents([...components, { component: '', toConsume: '', units: '' }]);
  const updateComponent = (idx, field, value) => {
    const updated = components.map((row, i) => i === idx ? { ...row, [field]: value } : row);
    setComponents(updated);
  };
  const removeComponent = idx => setComponents(components.filter((_, i) => i !== idx));
  const addWorkOrder = () => setWorkOrders([...workOrders, { operation: '', workCenter: '', duration: '' }]);
  const updateWorkOrder = (idx, field, value) => {
    const updated = workOrders.map((row, i) => i === idx ? { ...row, [field]: value } : row);
    setWorkOrders(updated);
  };
  const removeWorkOrder = idx => setWorkOrders(workOrders.filter((_, i) => i !== idx));

  const handleSave = () => {
    if (!finishedProduct || !quantity) {
      alert('Please fill all required fields.');
      return;
    }
    const newBOM = { id: bomId, finishedProduct, quantity: parseFloat(quantity), reference, components, workOrders };
    setBOMs(prev => prev.some(b => b.id === newBOM.id) ? prev.map(b => b.id === newBOM.id ? newBOM : b) : [...prev, newBOM]);
    setShowEdit(false);
  };

  const handleEdit = (bom) => { setEditingBOM(bom); setShowEdit(true); };
  const handleNew = () => { setEditingBOM(null); setShowEdit(true); };
  const handleCancel = () => { setShowEdit(false); };
  const handleBack = () => { setShowEdit(false); /* Add real navigation logic here */ };

  return (
    <div className="manufacturing-container">
      <a href="#" onClick={handleBack} className="back-link">‚Üê Back to Dashboard</a>
      
      {!showEdit ? (
        <>
          <section className="card">
            <div className="card-header">
              <h1>Bills of Materials</h1>
              <div className="actions">
                <input
                  type="text"
                  placeholder="Search Finished Product or New"
                  value={search}
                  onChange={e => setSearch(e.target.value)}
                  className="search-input"
                />
                <button onClick={handleNew} className="btn btn-primary">New</button>
              </div>
            </div>
            <table className="data-table">
              <thead>
                <tr>
                  <th>BOM ID</th>
                  <th>Finished Product</th>
                  <th>Quantity</th>
                  <th>Reference</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredBOMs.map(bom => (
                  <tr key={bom.id}>
                    <td>{bom.id}</td>
                    <td>{bom.finishedProduct}</td>
                    <td>{bom.quantity}</td>
                    <td>{bom.reference}</td>
                    <td><button onClick={() => handleEdit(bom)} className="btn btn-secondary">Edit</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>

          <section className="card">
            <div className="card-header">
              <h2>Manufacturing Order</h2>
              <div className="actions">
                <label htmlFor="bom-select">Select BOM</label>
                <select id="bom-select" value={selectedBOMId} onChange={e => setSelectedBOMId(e.target.value)}>
                  <option value="">Select</option>
                  {boms.map(bom => (
                    <option key={bom.id} value={bom.id}>{bom.id} - {bom.finishedProduct}</option>
                  ))}
                </select>
              </div>
            </div>
            
            <div className="mo-section">
                <h3>Components</h3>
                <div className="mo-header-grid">
                    <span>Component</span>
                    <span>To Consume</span>
                    <span>Units</span>
                </div>
                {/* Data for MO components would render here */}
            </div>

            <div className="mo-section">
                <h3>Work Orders</h3>
                <div className="mo-header-grid">
                    <span>Operation</span>
                    <span>Work Center</span>
                    <span>Expected Duration</span>
                </div>
                {/* Data for MO work orders would render here */}
            </div>
          </section>
        </>
      ) : (
        <section className="card">
            <h1>{isNew ? 'New' : 'Edit'} Bill of Materials</h1>
            {/* Form layout and tables for editing would go here */}
            {/* This part needs styling similar to the above tables and inputs */}
            <p>Edit/New form fields and tables go here...</p>
             <div>
               <button type="button" onClick={handleSave} className="btn btn-primary">Save</button>
               <button type="button" onClick={handleCancel} className="btn btn-secondary">Cancel</button>
             </div>
        </section>
      )}
    </div>
  );
};

export default BillOfMaterials;
