import React, { useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { useManufacturing } from '../context/ManufacturingContext';
import DashboardCard from '../components/DashboardCard';
import OrderTable from '../components/OrderTable';
import VoiceCommand from '../components/VoiceCommand';

const Dashboard = () => {
  const { manufacturingOrders, workOrders } = useManufacturing();
  const [selectedStatus, setSelectedStatus] = useState('All');
  const navigate = useNavigate();

  // Filter orders based on selected status
  const filteredOrders = useMemo(() => {
    if (selectedStatus === 'All') return manufacturingOrders;
    return manufacturingOrders.filter(order => {
      switch (selectedStatus) {
        case 'Draft': return order.status === 'Draft';
        case 'Confirmed': return order.status === 'Confirmed';
        case 'In-Progress': return order.status === 'In-Progress';
        case 'Done': return order.status === 'Done';
        case 'Cancelled': return order.status === 'Cancelled';
        default: return true;
      }
    });
  }, [manufacturingOrders, selectedStatus]);

  // Calculate summary data
  const summary = useMemo(() => {
    const total = manufacturingOrders.length;
    const active = manufacturingOrders.filter(o => o.status === 'In-Progress').length;
    const completed = manufacturingOrders.filter(o => o.status === 'Done').length;
    const draft = manufacturingOrders.filter(o => o.status === 'Draft').length;
    const confirmed = manufacturingOrders.filter(o => o.status === 'Confirmed').length;
    
    return {
      totalOrders: total,
      activeOrders: active,
      completedOrders: completed,
      draftOrders: draft,
      confirmedOrders: confirmed
    };
  }, [manufacturingOrders]);

  const handleVoiceCommand = (type, data) => {
    if (type === 'search') {
      // Handle search functionality
      console.log('Search for:', data);
    }
  };

  const handleVoiceAction = (action) => {
    switch (action) {
      case 'createManufacturingOrder':
        navigate('/manufacturing-orders?action=create');
        break;
      case 'createWorkOrder':
        navigate('/work-orders?action=create');
        break;
      default:
        console.log('Action:', action);
    }
  };

  const handleVoiceNavigate = (path) => {
    navigate(path);
  };

  const manufacturingOrderColumns = [
    { key: 'orderId', header: 'Order ID' },
    { key: 'product', header: 'Product' },
    { key: 'quantity', header: 'Quantity' },
    { key: 'startDate', header: 'Start Date' },
    { key: 'endDate', header: 'End Date' },
    { key: 'status', header: 'Status' }
  ];

  const breakdownColumns = [
    { key: 'operation', header: 'Operation' },
    { key: 'workCenter', header: 'Work Center' },
    { key: 'product', header: 'Product' },
    { key: 'quantity', header: 'Quantity' },
    { key: 'expectedDuration', header: 'Expected Duration' },
    { key: 'realDuration', header: 'Real Duration' },
    { key: 'status', header: 'Status' }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <div className="flex items-center gap-3">
          <button
            onClick={() => navigate('/app/manufacturing-orders?action=create')}
            className="btn-primary btn-lg"
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            New Manufacturing Order
          </button>
          <VoiceCommand
            onCommand={handleVoiceCommand}
            onNavigate={handleVoiceNavigate}
            onAction={handleVoiceAction}
          />
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <DashboardCard
          title="Total Orders"
          value={dashboardData.summary.totalOrders}
          icon="ðŸ“Š"
          color="blue"
          onClick={() => navigate('/manufacturing-orders')}
        />
        <DashboardCard
          title="Active Orders"
          value={dashboardData.summary.activeOrders}
          icon="âš¡"
          color="green"
          onClick={() => navigate('/manufacturing-orders?status=active')}
        />
        <DashboardCard
          title="Completed Orders"
          value={dashboardData.summary.completedOrders}
          icon="âœ…"
          color="purple"
          onClick={() => navigate('/manufacturing-orders?status=completed')}
        />
        <DashboardCard
          title="Total Value"
          value={`$${dashboardData.summary.totalValue.toLocaleString()}`}
          icon="ðŸ’°"
          color="yellow"
          onClick={() => navigate('/reports')}
        />
      </div>

      {/* Manufacturing Orders Overview */}
      <div className="card">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold text-gray-900">Manufacturing Orders</h2>
          <button
            onClick={() => navigate('/manufacturing-orders')}
            className="text-blue-600 hover:text-blue-800 text-sm font-medium"
          >
            View All
          </button>
        </div>
        <OrderTable
          data={dashboardData.manufacturingOrders}
          columns={manufacturingOrderColumns}
          onRowClick={(row) => navigate(`/manufacturing-orders/${row.id}`)}
          showActions={false}
        />
      </div>

      {/* Breakdown Section */}
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Breakdown</h2>
        <OrderTable
          data={dashboardData.workOrders}
          columns={breakdownColumns}
          onRowClick={(row) => navigate(`/work-orders/${row.id}`)}
          showActions={false}
        />
      </div>
    </div>
  );
};

export default Dashboard;
