import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useManufacturing } from '../context/ManufacturingContext';

const StockLedger = () => {
  const { stockItems, addStockItem, updateStockItem, deleteStockItem } = useManufacturing();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    product: '',
    unitCost: '',
    unit: 'pcs',
    onHand: '',
    freeToUse: '',
    incoming: '',
    outgoing: ''
  });
  
  const navigate = useNavigate();
  const params = useParams();

  // Check if we're in create mode
  React.useEffect(() => {
    if (params.mode === 'new') {
      setShowForm(true);
      setSelectedItem(null);
    }
  }, [params]);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredStockItems = stockItems.filter(item =>
    item.product?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.unit?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleCreateItem = () => {
    navigate('/stock-ledger/new');
  };

  const handleEditItem = (item) => {
    setSelectedItem(item);
    setFormData({
      product: item.product,
      unitCost: item.unitCost.toString(),
      unit: item.unit,
      onHand: item.onHand.toString(),
      freeToUse: item.freeToUse.toString(),
      incoming: item.incoming.toString(),
      outgoing: item.outgoing.toString()
    });
    setShowForm(true);
  };

  const handleSave = () => {
    const stockData = {
      product: formData.product,
      unitCost: parseFloat(formData.unitCost) || 0,
      unit: formData.unit,
      onHand: parseInt(formData.onHand) || 0,
      freeToUse: parseInt(formData.freeToUse) || 0,
      incoming: parseInt(formData.incoming) || 0,
      outgoing: parseInt(formData.outgoing) || 0
    };

    if (selectedItem) {
      updateStockItem(selectedItem.id, stockData);
    } else {
      addStockItem(stockData);
    }

    handleCancel();
  };

  const handleCancel = () => {
    setShowForm(false);
    setSelectedItem(null);
    navigate('/stock-ledger');
  };

  const handleDelete = (item) => {
    if (window.confirm(`Are you sure you want to delete ${item.product}?`)) {
      deleteStockItem(item.id);
    }
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(value);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white relative">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700 p-4">
        <div className="flex items-center justify-between">
          {/* Left side - Master Menu Button */}
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setShowMasterMenu(!showMasterMenu)}
              className="flex items-center space-x-2 px-3 py-2 bg-gray-700 hover:bg-gray-600 rounded border border-red-500 text-white transition-colors"
            >
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
              <span>Master Menu</span>
            </button>
            
            <span className="text-orange-400 text-sm">
              When clicked on Stock Ledger, from master menu<br />
              By default land on List View
            </span>
          </div>

          {/* Right side - Search and Controls */}
          <div className="flex items-center space-x-4">
            <span className="text-orange-400 text-sm">
              Allow user to search based on Product and Unit
            </span>
            <div className="flex items-center space-x-2">
              <button className="p-2 bg-gray-700 hover:bg-gray-600 rounded border border-red-500">
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </button>
              <button className="p-2 bg-gray-700 hover:bg-gray-600 rounded border border-red-500">
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              </button>
              <button className="p-2 bg-gray-700 hover:bg-gray-600 rounded border border-red-500">
                <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Master Menu Sidebar */}
      {showMasterMenu && (
        <>
          <div 
            className="fixed inset-0 bg-black bg-opacity-50 z-10"
            onClick={() => setShowMasterMenu(false)}
          />
          <div className="fixed left-4 top-20 w-48 bg-gray-800 border border-red-500 rounded z-20">
            <div className="p-3 border-b border-gray-700">
              <div className="flex items-center justify-between">
                <span className="text-orange-400 font-medium">Master Menu</span>
                <button
                  onClick={() => setShowMasterMenu(false)}
                  className="text-gray-400 hover:text-white"
                >
                  <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
            </div>
            <div className="py-2">
              <button
                onClick={() => handleMasterMenuNavigation('/manufacturing-orders')}
                className="w-full text-left px-4 py-2 text-white hover:bg-gray-700 border-b border-gray-700"
              >
                Manufacturing Orders
              </button>
              <button
                onClick={() => handleMasterMenuNavigation('/work-orders')}
                className="w-full text-left px-4 py-2 text-white hover:bg-gray-700 border-b border-gray-700"
              >
                Work Orders
              </button>
              <button
                onClick={() => handleMasterMenuNavigation('/bills-of-materials')}
                className="w-full text-left px-4 py-2 text-white hover:bg-gray-700 border-b border-gray-700"
              >
                Bills of Materials
              </button>
              <button
                onClick={() => handleMasterMenuNavigation('/work-center')}
                className="w-full text-left px-4 py-2 text-white hover:bg-gray-700 border-b border-gray-700"
              >
                Work Center
              </button>
              <button
                onClick={() => handleMasterMenuNavigation('/stock-ledger')}
                className="w-full text-left px-4 py-2 text-orange-400 bg-gray-700 border-b border-gray-700"
              >
                Stock Ledger
              </button>
            </div>
          </div>
        </>
      )}

      {/* Main Content */}
      <div className="p-6">
        {/* Stock Ledger Table */}
        <div className="bg-gray-800 border border-red-500 rounded-lg mb-6">
          {/* Table Header */}
          <div className="bg-gray-700 px-4 py-3 border-b border-red-500">
            <div className="flex items-center justify-between">
              <h2 className="text-xl font-bold text-orange-400">Stock Ledger</h2>
              <button className="px-4 py-2 bg-gray-600 hover:bg-gray-500 border border-red-500 rounded text-white">
                New
              </button>
            </div>
          </div>

          {/* Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="bg-gray-700 border-b border-red-500">
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">Product</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">Unit Cost</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">Unit</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">Total Value</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">On Hand</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">Free to Use</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium border-r border-red-500">Incoming</th>
                  <th className="px-4 py-3 text-left text-orange-400 font-medium">Outgoing</th>
                </tr>
              </thead>
              <tbody>
                {filteredStockItems.map((item, index) => (
                  <tr key={item.id} className="border-b border-red-500 hover:bg-gray-700">
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.product}</td>
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.unitCost}</td>
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.unit}</td>
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.totalValue}</td>
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.onHand}</td>
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.freeToUse}</td>
                    <td className="px-4 py-3 text-white border-r border-red-500">{item.incoming}</td>
                    <td className="px-4 py-3 text-white">{item.outgoing}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Form Section */}
        <div className="bg-gray-800 border border-red-500 rounded-lg">
          {/* Form Header */}
          <div className="bg-gray-700 px-4 py-3 border-b border-red-500">
            <div className="flex items-center justify-between">
              <div className="flex space-x-4">
                <button
                  onClick={handleBack}
                  className="px-4 py-2 bg-gray-600 hover:bg-gray-500 border border-red-500 rounded text-white"
                >
                  Back
                </button>
                <button
                  onClick={handleSave}
                  className="px-4 py-2 bg-gray-600 hover:bg-gray-500 border border-red-500 rounded text-white"
                >
                  Save
                </button>
              </div>
            </div>
          </div>

          {/* Form Content */}
          <div className="p-6">
            <div className="grid grid-cols-2 gap-8">
              {/* Left Column */}
              <div className="space-y-6">
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Product</label>
                  <input
                    type="text"
                    value={formData.product}
                    onChange={(e) => setFormData({...formData, product: e.target.value})}
                    className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                  />
                </div>
                
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Unit Cost</label>
                  <input
                    type="text"
                    value={formData.unitCost}
                    onChange={(e) => setFormData({...formData, unitCost: e.target.value})}
                    className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                  />
                </div>
                
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Unit</label>
                  <div className="relative">
                    <input
                      type="text"
                      value={formData.unit}
                      onChange={(e) => setFormData({...formData, unit: e.target.value})}
                      className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                    />
                    <span className="absolute right-0 top-1 text-orange-400 text-sm">Selection field</span>
                  </div>
                </div>
                
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Total Value</label>
                  <div className="relative">
                    <input
                      type="text"
                      value={formData.totalValue}
                      onChange={(e) => setFormData({...formData, totalValue: e.target.value})}
                      className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                    />
                    <span className="absolute right-0 -bottom-6 text-orange-400 text-sm">Read-only : on Hand * unit cost</span>
                  </div>
                </div>
              </div>

              {/* Right Column */}
              <div className="space-y-6">
                <div>
                  <label className="block text-orange-400 font-medium mb-2">On Hand</label>
                  <input
                    type="text"
                    value={formData.onHand}
                    onChange={(e) => setFormData({...formData, onHand: e.target.value})}
                    className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                  />
                </div>
                
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Free to Use</label>
                  <input
                    type="text"
                    value={formData.freeToUse}
                    onChange={(e) => setFormData({...formData, freeToUse: e.target.value})}
                    className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                  />
                </div>
                
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Outgoing</label>
                  <input
                    type="text"
                    value={formData.outgoing}
                    onChange={(e) => setFormData({...formData, outgoing: e.target.value})}
                    className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                  />
                </div>
                
                <div>
                  <label className="block text-orange-400 font-medium mb-2">Incoming</label>
                  <input
                    type="text"
                    value={formData.incoming}
                    onChange={(e) => setFormData({...formData, incoming: e.target.value})}
                    className="w-full bg-transparent border-b border-red-500 text-white px-2 py-1 focus:outline-none focus:border-orange-400"
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StockLedger;
