import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error('useAuth must be used within an AuthProvider');
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
      } catch {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }
    setLoading(false);
  }, []);

  // Login function
  const login = async (userId, password) => {
    try {
      // Make sure your backend expects "username" or "email"
      const payload = { username: userId, password }; 
      const response = await api.post('/auth/login', payload);
      const { token, user: userData } = response.data;

      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);

      return { success: true, user: userData };
    } catch (error) {
      console.error('Login error:', error.response?.data || error);
      return {
        success: false,
        error: error.response?.data?.message || 'Login failed. Check credentials.'
      };
    }
  };

  // Signup function
  const signup = async (signupData) => {
    try {
      const payload = {
        username: signupData.userId,
        name: signupData.name,
        email: signupData.email,
        password: signupData.password,
        role: signupData.role || 'User'
      };
      await api.post('/auth/register', payload);
      return await login(signupData.userId, signupData.password);
    } catch (error) {
      console.error('Signup error:', error.response?.data || error);
      return {
        success: false,
        error: error.response?.data?.message || 'Signup failed.'
      };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, signup, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
