    const mockWorkOrders = [
      { id: 1, operation: 'Assembly', workCenter: 'Assembly Line 1', duration: 120, realDuration: 0, status: 'Pending' },
      { id: 2, operation: 'Quality Check', workCenter: 'QC Station', duration: 30, realDuration: 0, status: 'Pending' },
      { id: 3, operation: 'Packaging', workCenter: 'Packaging Line', duration: 45, realDuration: 0, status: 'Pending' }
    ];

    setFormData(prev => ({
      ...prev,
      components: mockComponents,
      workOrders: mockWorkOrders
    }));
  };

  const loadOrders = async () => {
    try {
      setLoading(true);
      const data = await manufacturingAPI.getManufacturingOrders();
      setOrders(data);
    } catch (error) {
