      setOrders([
        { 
          id: 1, 
          reference: 'MO-001', 
          finishedProduct: 'Widget A', 
          quantity: 100, 
          unit: 'pcs',
          startDate: '2024-01-15', 
          endDate: '2024-01-20', 
          status: 'Draft', 
          componentStatus: 'Ready',
          progress: 0, 
          goalDate: '2024-01-20',
          assignee: null,
          orderId: 'MO-001',
          product: 'Widget A'
        },
        { 
          id: 2, 
          reference: 'MO-002', 
          finishedProduct: 'Widget B', 
          quantity: 50, 
          unit: 'pcs',
          startDate: '2024-01-16', 
          endDate: '2024-01-22', 
          status: 'Confirmed', 
          componentStatus: 'In Progress',
          progress: 45, 
          goalDate: '2024-01-22',
          assignee: 'John Doe',
          orderId: 'MO-002',
          product: 'Widget B'
        },
        { 
          id: 3, 
          reference: 'MO-003', 
          finishedProduct: 'Widget C', 
          quantity: 75, 
          unit: 'pcs',
          startDate: '2024-01-10', 
          endDate: '2024-01-18', 
          status: 'In Progress', 
          componentStatus: 'Complete',
          progress: 100, 
          goalDate: '2024-01-18',
          assignee: 'Jane Smith',
          orderId: 'MO-003',
          product: 'Widget C'
        },
        { 
          id: 4, 
          reference: 'MO-004', 
          finishedProduct: 'Widget D', 
          quantity: 200, 
          unit: 'pcs',
          startDate: '2024-01-20', 
          endDate: '2024-01-25', 
          status: 'To Close', 
          componentStatus: 'Ready',
          progress: 0, 
          goalDate: '2024-01-25',
          assignee: 'Bob Johnson',
          orderId: 'MO-004',
          product: 'Widget D'
        },
        { 
          id: 5, 
          reference: 'MO-005', 
          finishedProduct: 'Widget E', 
          quantity: 150, 
          unit: 'pcs',
          startDate: '2024-01-05', 
          endDate: '2024-01-12', 
          status: 'Confirmed', 
