      confirmed: myOrders.filter(o => o.status === 'Confirmed').length,
      inProgress: myOrders.filter(o => o.status === 'In Progress').length,
      toClose: myOrders.filter(o => o.status === 'To Close').length,
      late: myOrders.filter(isLate).length
