    return { all: allCounts, my: myCounts };
  };

  const filteredOrders = getFilteredOrders();
  const counts = getOrderCounts();

  const handleCreateOrder = () => {
    setFormData({
      product: '',
      quantity: '',
      startDate: '',
      endDate: '',
      status: 'New',
