import React, { useState, useEffect } from 'react';

// Dummy stock ledger data for Finished Product dropdown
const stockLedger = [
  { id: 1, name: 'Widget A' },
  { id: 2, name: 'Widget B' },
  { id: 3, name: 'Widget C' },
];

function generateBOMId(lastId) {
  const next = (parseInt(lastId?.split('-')[1] || '0', 10) + 1).toString().padStart(6, '0');
  return `BOM-${next}`;
}

const BillOFmaterials = () => {
  const [boms, setBOMs] = useState([]);
  const [search, setSearch] = useState('');
  const [showEdit, setShowEdit] = useState(false);
  const [editingBOM, setEditingBOM] = useState(null);

  // Manufacturing Order state
  const [selectedBOMId, setSelectedBOMId] = useState('');
  const [moComponents, setMoComponents] = useState([]);
  const [moWorkOrders, setMoWorkOrders] = useState([]);

  // Form states
  const isNew = !editingBOM;
  const [bomId, setBomId] = useState('');
  const [finishedProduct, setFinishedProduct] = useState('');
  const [quantity, setQuantity] = useState('');
  const [reference, setReference] = useState('');
  const [components, setComponents] = useState([]);
  const [workOrders, setWorkOrders] = useState([]);

  // When editing, populate form fields
  useEffect(() => {
    if (showEdit) {
      if (editingBOM) {
        setBomId(editingBOM.id);
        setFinishedProduct(editingBOM.finishedProduct);
        setQuantity(editingBOM.quantity);
        setReference(editingBOM.reference);
        setComponents(editingBOM.components || []);
        setWorkOrders(editingBOM.workOrders || []);
      } else {
        setBomId(generateBOMId(boms.length ? boms[boms.length - 1].id : null));
        setFinishedProduct('');
        setQuantity('');
        setReference('');
        setComponents([]);
        setWorkOrders([]);
      }
    }
  }, [showEdit, editingBOM, boms]);

  // Manufacturing Order: auto-populate tables when BOM selected
  useEffect(() => {
    const bom = boms.find(b => b.id === selectedBOMId);
    if (bom) {
      setMoComponents(bom.components || []);
      setMoWorkOrders(bom.workOrders || []);
    } else {
      setMoComponents([]);
      setMoWorkOrders([]);
    }
  }, [selectedBOMId, boms]);

  // List view filter
  const filteredBOMs = boms.filter(bom =>
    bom.finishedProduct.toLowerCase().includes(search.toLowerCase()) ||
    bom.reference.toLowerCase().includes(search.toLowerCase())
  );

  // Components Table handlers
  const addComponent = () => setComponents([...components, { component: '', toConsume: '', units: '' }]);
  const updateComponent = (idx, field, value) => {
    const updated = components.map((row, i) => i === idx ? { ...row, [field]: value } : row);
    setComponents(updated);
  };
  const removeComponent = idx => setComponents(components.filter((_, i) => i !== idx));

  // Work Orders Table handlers
  const addWorkOrder = () => setWorkOrders([...workOrders, { operation: '', workCenter: '', duration: '' }]);
  const updateWorkOrder = (idx, field, value) => {
    const updated = workOrders.map((row, i) => i === idx ? { ...row, [field]: value } : row);
    setWorkOrders(updated);
  };
  const removeWorkOrder = idx => setWorkOrders(workOrders.filter((_, i) => i !== idx));

  // Save BOM
  const handleSave = () => {
    if (!finishedProduct || !quantity || reference.length > 8) {
      alert('Please fill all fields correctly.');
      return;
    }
    const newBOM = {
      id: bomId,
      finishedProduct,
      quantity: parseFloat(quantity),
      reference,
      components,
      workOrders,
    };
    setBOMs(prev =>
      prev.some(b => b.id === newBOM.id)
        ? prev.map(b => b.id === newBOM.id ? newBOM : b)
        : [...prev, newBOM]
    );
    setShowEdit(false);
  };

  // Edit BOM
  const handleEdit = (bom) => {
    setEditingBOM(bom);
    setShowEdit(true);
  };

  // New BOM
  const handleNew = () => {
    setEditingBOM(null);
    setShowEdit(true);
  };

  // Cancel form
  const handleCancel = () => {
    setShowEdit(false);
  };

  return (
    <div style={{ padding: 24 }}>
      {!showEdit && (
        <>
          {/* BOM List View */}
          <div>
            <h2>Bills of Materials</h2>
            <input
              type="text"
              placeholder="Search Finished Product or Reference"
              value={search}
              onChange={e => setSearch(e.target.value)}
              style={{ marginRight: 8 }}
            />
            <button onClick={handleNew}>New</button>
            <table border="1" cellPadding="4" style={{ marginTop: 16, width: '100%' }}>
              <thead>
                <tr>
                  <th>BOM ID</th>
                  <th>Finished Product</th>
                  <th>Quantity</th>
                  <th>Reference</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredBOMs.map(bom => (
                  <tr key={bom.id}>
                    <td>{bom.id}</td>
                    <td>{bom.finishedProduct}</td>
                    <td>{bom.quantity}</td>
                    <td>{bom.reference}</td>
                    <td>
                      <button onClick={() => handleEdit(bom)}>Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <hr style={{ margin: '32px 0' }} />
          {/* Manufacturing Order Page */}
          <div>
            <h2>Manufacturing Order</h2>
            <div>
              <label>Select BOM</label>
              <select value={selectedBOMId} onChange={e => setSelectedBOMId(e.target.value)} style={{ marginLeft: 8 }}>
                <option value="">Select</option>
                {boms.map(bom => (
                  <option key={bom.id} value={bom.id}>{bom.id} - {bom.finishedProduct}</option>
                ))}
              </select>
            </div>
            <h3>Components</h3>
            <table border="1" cellPadding="4" width="100%">
              <thead>
                <tr>
                  <th>Component</th>
                  <th>To Consume</th>
                  <th>Units</th>
                </tr>
              </thead>
              <tbody>
                {moComponents.map((row, idx) => (
                  <tr key={idx}>
                    <td>{row.component}</td>
                    <td>{row.toConsume}</td>
                    <td>{row.units}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <h3>Work Orders</h3>
            <table border="1" cellPadding="4" width="100%">
              <thead>
                <tr>
                  <th>Operation</th>
                  <th>Work Center</th>
                  <th>Expected Duration</th>
                </tr>
              </thead>
              <tbody>
                {moWorkOrders.map((row, idx) => (
                  <tr key={idx}>
                    <td>{row.operation}</td>
                    <td>{row.workCenter}</td>
                    <td>{row.duration}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
      {showEdit && (
        <div>
          <h2>{isNew ? 'New' : 'Edit'} Bill of Materials</h2>
          <div>
            <label>BOM ID</label>
            <input value={bomId} readOnly style={{ marginLeft: 8, marginBottom: 8 }} />
          </div>
          <div>
            <label>Finished Product</label>
            <select value={finishedProduct} onChange={e => setFinishedProduct(e.target.value)} style={{ marginLeft: 8 }}>
              <option value="">Select</option>
              {stockLedger.map(item => (
                <option key={item.id} value={item.name}>{item.name}</option>
              ))}
            </select>
          </div>
          <div>
            <label>Quantity</label>
            <input
              type="number"
              step="0.01"
              value={quantity}
              onChange={e => setQuantity(e.target.value)}
              style={{ marginLeft: 8 }}
            />
          </div>
          <div>
            <label>Reference</label>
            <input
              type="text"
              maxLength={8}
              value={reference}
              onChange={e => setReference(e.target.value)}
              style={{ marginLeft: 8 }}
            />
          </div>
          {/* Components Table */}
          <h3>Components</h3>
          <table border="1" cellPadding="4" width="100%">
            <thead>
              <tr>
                <th>Component</th>
                <th>To Consume</th>
                <th>Units</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {components.map((row, idx) => (
                <tr key={idx}>
                  <td>
                    <input
                      value={row.component}
                      onChange={e => updateComponent(idx, 'component', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      step="0.01"
                      value={row.toConsume}
                      onChange={e => updateComponent(idx, 'toConsume', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      value={row.units}
                      onChange={e => updateComponent(idx, 'units', e.target.value)}
                    />
                  </td>
                  <td>
                    <button type="button" onClick={() => removeComponent(idx)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button type="button" onClick={addComponent} style={{ marginTop: 4 }}>Add Component</button>
          {/* Work Orders Table */}
          <h3>Work Orders</h3>
          <table border="1" cellPadding="4" width="100%">
            <thead>
              <tr>
                <th>Operation</th>
                <th>Work Center</th>
                <th>Expected Duration</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {workOrders.map((row, idx) => (
                <tr key={idx}>
                  <td>
                    <input
                      value={row.operation}
                      onChange={e => updateWorkOrder(idx, 'operation', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      value={row.workCenter}
                      onChange={e => updateWorkOrder(idx, 'workCenter', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      step="0.01"
                      value={row.duration}
                      onChange={e => updateWorkOrder(idx, 'duration', e.target.value)}
                    />
                  </td>
                  <td>
                    <button type="button" onClick={() => removeWorkOrder(idx)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button type="button" onClick={addWorkOrder} style={{ marginTop: 4 }}>Add Work Order</button>
          <div style={{ marginTop: 16 }}>
            <button type="button" onClick={handleSave}>Save</button>
            <button type="button" onClick={handleCancel} style={{ marginLeft: 8 }}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BillOFmaterials;
